mapper.select:
select * from (select rownum rownum_page, page_1.* from (
select * from (select sysuser.*, (select t.INFO from DICTINFO t where t.dictcode = sysuser.groupid and t.typecode = 's01') groupname, decode(groupid, 1, (select mc from userjd where userjd.id = sysuser.sysid), 2, (select mc from userjd where userjd.id = sysuser.sysid), 3, (select mc from useryy where useryy.id = sysuser.sysid), 4, (select mc from usergys where usergys.id = sysuser.sysid)) sysmc from sysuser) sysuser
) page_1 where rownum <= ${pageQuery.PageQuery_end}) page_2 where page_2.rownum_page > ${pageQuery.PageQuery_start}

select bss_sys_operate.method actionUrl, bss_sys_operate.operatename operationName, bss_sys_operate.operateid operationId from bss_sys_operate
where bss_sys_operate.operateid in (
select bss_sys_roleoperate.operateid from bss_sys_role, bss_sys_rolesys, bss_sys_rolenode, bss_sys_rolemodule, bss_sys_roleoperate
where bss_sys_role.roleid = bss_sys_rolesys.roleid and bss_sys_rolenode.rsid = bss_sys_rolesys.rsid and bss_sys_rolemodule.rnid = bss_sys_rolenode.rnid and bss_sys_roleoperate.rmid = bss_sys_rolemodule.rmid and bss_sys_role.roleid = #{roleid}
)
